schema:
type Query {
  adminPanel: AdminPanel
}

type AdminPanel {
  stats: AdminStats!
  users: [User!]!
}

type AdminStats {
  totalUsers: Int!
  activeUsers: Int!
  totalRevenue: Float!
}

type User {
  id: String!
  email: String!
}

request.query:
query {
  adminPanel {
    stats {
      totalUsers
      activeUsers
      totalRevenue
    }
  }
}

