schema:
type Query {
  users(limit: Int, offset: Int): [User!]!
  user(id: String!): User
  search(term: String!): SearchResult!
}

type User {
  id: ID!
  name: String!
  email: String!
  posts(limit: Int): [Post!]!
  followers(limit: Int): [User!]!
  following(limit: Int): [User!]!
  profile: UserProfile!
}

type UserProfile {
  bio: String
  avatar: String
  website: String
  joinedAt: String!
}

type Post {
  id: ID!
  title: String!
  content: String!
  likes: Int!
  comments(limit: Int): [Comment!]!
  author: User!
  createdAt: String!
}

type Comment {
  id: ID!
  text: String!
  author: User!
  likes: Int!
}

type SearchResult {
  users: [User!]!
  posts: [Post!]!
  total: Int!
}

request.query:
query ComplexSearch($searchTerm: String!, $userLimit: Int!, $postLimit: Int!) {
  search(term: $searchTerm) {
    total
    users(limit: $userLimit) {
      id
      name
      email
      profile {
        bio
        avatar
        joinedAt
      }
      recentPosts: posts(limit: 3) {
        id
        title
        likes
      }
      followerCount: followers(limit: 100) {
        id
      }
    }
    posts(limit: $postLimit) {
      id
      title
      content
      likes
      author {
        id
        name
        profile {
          avatar
        }
      }
      topComments: comments(limit: 5) {
        id
        text
        likes
        author {
          id
          name
        }
      }
    }
  }
}

