schema:
type Query {
  user(id: String!): User
}

type User {
  id: String!
  name: String!
  profile: Profile!
}

type Profile {
  bio: String
  settings: Settings!
}

type Settings {
  preferences: Preferences!
  notifications: NotificationSettings!
}

type Preferences {
  theme: String!
  language: String!
  timezone: Timezone!
}

type Timezone {
  name: String!
  offset: Int!
}

type NotificationSettings {
  email: Boolean!
  push: Boolean!
}

request.query:
query GetUserDeepNested($userId: String!) {
  user(id: $userId) {
    id
    name
    profile {
      bio
      settings {
        preferences {
          theme
          language
          timezone {
            name
            offset
          }
        }
        notifications {
          email
          push
        }
      }
    }
  }
}

