schema:
type Mutation {
  createPost(input: CreatePostInput!): Post!
}

input CreatePostInput {
  title: String!
  content: String!
  tags: [String!]!
}

type Post {
  id: ID!
  title: String!
  content: String!
  tags: [String!]!
  createdAt: String!
}

request.query:
mutation CreatePost($input: CreatePostInput!) {
  createPost(input: $input) {
    id
    title
    content
    tags
    createdAt
  }
}

body.query:
mutation CreatePost($input: CreatePostInput!) {
  createPost(input: $input) {
    id
    title
    content
    tags
    createdAt
  }
}
