schema:
type Query {
  _entities(representations: [_Any!]!): [_Entity]!
  _service: _Service!
  product(sku: String!, category: String!): Product
}

union _Entity = Product

type Product @key(fields: "sku category") {
  sku: String!
  category: String!
  name: String!
  description: String!
  price: Float!
  stock: Int!
}

scalar _Any

type _Service {
  sdl: String!
}

request.query:
query {
  _entities(representations: [{__typename: "Product", sku: "ABC123", category: "electronics"}]) {
    ... on Product {
      sku
      category
      name
      description
      price
      stock
    }
  }
}

