schema:
type Query {
  _entities(representations: [_Any!]!): [_Entity]!
  _service: _Service!
}

union _Entity = Customer

type Customer @key(fields: "id") {
  id: ID!
  firstName: String!
  lastName: String!
  email: String!
}

scalar _Any

type _Service {
  sdl: String!
}

request.query:
query {
  _entities(representations: [{__typename: "Customer", id: "999999"}]) {
    ... on Customer {
      id
      firstName
      lastName
      email
    }
  }
}

