schema:
type Mutation {
  deleteUser(id: ID!): DeleteResult!
}

type DeleteResult {
  success: Boolean!
  message: String!
  deletedId: ID!
}

request.query:
mutation DeleteUser($id: ID!) {
  deleteUser(id: $id) {
    success
    message
    deletedId
  }
}

body.query:
