schema:
type Mutation {
  updateUser(id: ID!, input: UpdateUserInput!): User!
}

input UpdateUserInput {
  name: String
  email: String
  role: String
}

type User {
  id: ID!
  name: String!
  email: String!
  role: String
  updatedAt: String!
}

request.query:
mutation UpdateUser($id: ID!, $input: UpdateUserInput!) {
  updateUser(id: $id, input: $input) {
    id
    name
    email
    role
    updatedAt
  }
}
