schema:
type Query {
  user(id: String!): User
  post(id: String!): Post
}

type User {
  id: String!
  name: String!
  email: String!
}

type Post {
  id: String!
  title: String!
  content: String!
  author_id: String!
}

body.query:
query GetUser1 {
  user(id: "user-1") {
    id
    name
    email
  }
}

query GetUser2 {
  user(id: "user-2") {
    id
    name
    email
  }
}

query GetPost1 {
  post(id: "post-1") {
    id
    title
    author_id
  }
}
