schema:
type Query {
  user(id: String!): User
}

type User {
  id: String!
  name: String!
  email: String!
  profile: Profile!
}

type Profile {
  bio: String
  avatar: String
  location: String
}

request.query:
query GetUser($userId: String!) {
  user(id: $userId) {
    id
    name
    email
    profile {
      bio
      location
    }
  }
}
