schema:
scalar DateTime

type Query {
  events(since: DateTime, until: DateTime): [Event!]!
}

type Event {
  id: ID!
  title: String!
  scheduledAt: DateTime!
  completedAt: DateTime
}

request.query:
query GetEvents($since: DateTime, $until: DateTime) {
  events(since: $since, until: $until) {
    id
    title
    scheduledAt
    completedAt
  }
}

body.query:
query GetEvents($since: DateTime, $until: DateTime) {
  events(since: $since, until: $until) {
    id
    title
    scheduledAt
    completedAt
  }
}
