schema:
enum OrderStatus {
  PENDING
  SHIPPED
  DELIVERED
  CANCELLED
}

type Subscription {
  orderUpdated(status: OrderStatus): Order!
}

type Order {
  id: ID!
  orderId: String!
  status: OrderStatus!
  amount: Float!
  updatedAt: String!
}

request.query:
subscription OnOrderUpdated($status: OrderStatus) {
  orderUpdated(status: $status) {
    id
    orderId
    status
    amount
    updatedAt
  }
}

body.query:
subscription OnOrderUpdated($status: OrderStatus) {
