schema:
type Subscription {
  messageAdded: Message!
  userOnline: UserStatus!
}

type Message {
  id: ID!
  text: String!
  author: String!
}

type UserStatus {
  userId: ID!
  username: String!
  isOnline: Boolean!
  lastSeen: String!
}

request.query:
subscription MultiStream {
  messageAdded {
    id
    text
    author
  }
  userOnline {
    userId
    username
    isOnline
    lastSeen
  }
}

body.query:
