schema:
scalar Upload

type Mutation {
  uploadProfilePicture(userId: ID!, file: Upload!): User!
}

type User {
  id: ID!
  name: String!
  email: String!
  profilePicture: File!
}

type File {
  id: ID!
  filename: String!
  mimetype: String!
  size: Int!
}

request.query:
mutation UploadProfile($userId: ID!, $file: Upload!) {
  uploadProfilePicture(userId: $userId, file: $file) {
    id
    name
    email
    profilePicture {
      id
      filename
      mimetype
      size
    }
  }
}
