schema:
type Query {
  searchUsers(limit: Int!, offset: Int): [User!]!
}

type User {
  id: ID!
  name: String!
  email: String!
}

request.query:
query SearchUsers($limit: Int!, $offset: Int) {
  searchUsers(limit: $limit, offset: $offset) {
    id
    name
    email
  }
}

body.query:
query SearchUsers($limit: Int!, $offset: Int) {
  searchUsers(limit: $limit, offset: $offset) {
    id
    name
    email
  }
}
