schema:
type Mutation {
  registerUser(input: UserRegistrationInput!): RegistrationResult!
}

input UserRegistrationInput {
  username: String!
  email: String!
  password: String!
  firstName: String
  lastName: String
}

type RegistrationResult {
  success: Boolean!
  userId: ID
  message: String!
}

request.query:
mutation Register($input: UserRegistrationInput!) {
  registerUser(input: $input) {
    success
    userId
    message
  }
}

body.query:
mutation Register($input: UserRegistrationInput!) {
  registerUser(input: $input) {
    success
    userId
    message
  }
}
